package com.mycompany.app;

import java.util.Scanner;

public class App {
    public static void main(String[] args) {
        Scanner scan = new Scanner(System.in);

        System.out.println("Hello Remote World!");

        // Ask for the user's name
        System.out.print("Enter your name: ");
        String name = scan.nextLine();

        // Greet the user
        System.out.println("Hello, " + name + "! Welcome to the Remote World!");

        // Ask for the user's age
        System.out.print("Enter your age: ");
        int age = scan.nextInt();
        scan.nextLine(); // Consume newline

        if (age < 18) {
            System.out.println("You're quite young, " + name + "!");
        } else {
            System.out.println("Nice to meet you, " + name + "!");
        }

        // Ask for the user's favorite programming language
        System.out.print("Enter your favorite programming language: ");
        String language = scan.nextLine();

        System.out.println(language + " is a great choice!");

        // Simple calculator
        System.out.println("Let's do some basic arithmetic.");
        System.out.print("Enter the first number: ");
        double num1 = scan.nextDouble();

        System.out.print("Enter the second number: ");
        double num2 = scan.nextDouble();

        System.out.println("Choose an operation (+, -, *, /): ");
        char operation = scan.next().charAt(0);

        double result = 0;
        switch (operation) {
            case '+':
                result = num1 + num2;
                break;
            case '-':
                result = num1 - num2;
                break;
            case '*':
                result = num1 * num2;
                break;
            case '/':
                if (num2 != 0) {
                    result = num1 / num2;
                } else {
                    System.out.println("Error: Division by zero is not allowed.");
                    scan.close();
                    return;
                }
                break;
            default:
                System.out.println("Invalid operation.");
                scan.close();
                return;
        }

        System.out.println("The result of the operation is: " + result);

        scan.close();
    }
}
